// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

interface IMarketplace {
    event Bids(uint256 indexed itemId, address bidder, uint256 amount);
    event Sales(uint256 indexed itemId, address indexed owner, uint256 amount, uint256 quantity, uint256 indexed tokenId);
    event Closes(uint256 indexed itemId);
    event Listings(
        uint256 indexed itemId,
        address indexed nftContract,
        uint256 indexed tokenId,
        address seller,
        address receiver,
        address owner,
        uint256 price,
        bool sold
    );
    struct MarketItem {
        uint256 itemId;
        address nftContract;
        uint256 tokenId;
        address seller;
        address owner;
        uint256 price;
        uint256 quantity;
        bool sold;
        address receiver;
    }
}

contracts/interfaces/ITokenSale.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

///
/// @notice A token seller is a contract that can sell tokens to a token buyer.
/// The token buyer can buy tokens from the seller by paying a certain amount
/// of base currency to receive a certain amount of erc1155 tokens. the number
/// of tokens that can be bought is limited by the seller - the seller can
/// specify the maximum number of tokens that can be bought per transaction
/// and the maximum number of tokens that can be bought in total for a given
/// address. The seller can also specify the price of erc1155 tokens and how
/// that price increases per successful transaction.
interface ITokenSale {

    struct TokenSaleEntry {
        address payable receiver;
        address sourceToken;
        uint256 sourceTokenId;
        address token;
        uint256 quantity;
        uint256 price;
        uint256 quantitySold;
    }

    event TokenSaleSet(address indexed token, uint256 indexed tokenId, uint256 price, uint256 quantity);
    event TokenSold(address indexed buyer, address indexed tokenAddress, uint256 indexed tokenId, uint256 salePrice);
    event TokensSet(address indexed tokenAddress, ITokenSale.TokenSaleEntry tokens);

}

contracts/interfaces/IForge.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "./ITokenDefinitions.sol";
import "./IRequirement.sol";

// defines the forge. The forge contains items that can be minted in exchange for input items
struct ForgeDefinition {
    // forge identifier
    uint256 forgeId;
    // forge symbol, name, description
    string name;
    string symbol;
    string description;
    // max number of forge operations
    uint256 maxForgeCount;
    // number of times this forge has been used
    uint256 forgedCount;
    // fee for each forge operation
    uint256 forgeFee;
}

// defines a single requirement for a forge item
struct ForgeItemRequirement {
    // the token address of the item
    address tokenAddress;
    // the token id of the item
    uint256 tokenId;
    // the amount of the item required
    uint256 amount;
}

// defines each item that can be forged
struct ForgeItemDefinition {

    // item name, symbol, description
    string name;
    string symbol;
    string description;

    // requirements for this item
    ForgeItemRequirement[] requirements;

    // probability of this item being forged, if multiple items are possible, the probability is the sum of all probabilities
    uint256 probability;
    // number of times this item can be forged
    uint256 maxForgeCount;
    // number of times this item has been forged
    uint256 forgedCount;
    // fee to be paid for forging this item. overrides the forge fee of the forge if nonzero
    uint256 forgeFee;
    // token hash to mint for this item. if zero, then the token hash is autogenerated
    uint256 tokenHash;
}

/// @notice a crafting matrix describes a set of items that can be crafted
interface IForge {

    /// @notice create a new forge
    /// @param name the name of the forge
    /// @param symbol the symbol of the forge
    /// @param description the description of the forge
    /// @param items the items that can be forged
    function createForge(string calldata name, string calldata symbol, string calldata description, ForgeItemDefinition[] calldata items) external;

    /// @notice get the forge definition
    /// @param symbol the symbol of the forge
    function getForge(string calldata symbol) external view returns (ForgeDefinition memory);

    // function _setForge(ForgeDefinition calldata forge) internal;

    /// @notice get the list of forgeable items
    function forgeableItems() external view returns (ForgeItemDefinition[] memory);

    /// @notice add a new forgeable item
    /// @param item the item to add
    /// @return the index of the item
    function addForgeableItem(ForgeItemDefinition calldata item) external returns (uint256);

    /// @notice forge an item
    /// @param symbol the symbol of the forge
    /// @param inputItems the input items
    /// @return the forged item
    function forgeAnyItem(string calldata symbol, uint256[] calldata inputItems) external returns (ForgeItemDefinition memory);

    /// @notice forge an item
    /// @param symbol the symbol of the forge
    /// @param index the index of the item to forge
    /// @param inputItems the input items
    /// @return the forged item
    function forgeItemAtIndex(string calldata symbol, uint256 index, uint256[] calldata inputItems) external returns (ForgeItemDefinition memory);

    /// @notice get the hash of a forge
    /// @param symbol the symbol of the forge
    function getForgeHash(string calldata symbol) external view returns (uint256);

    /// @notice get the forge by hash
    /// @param hash the hash of the forge
    function getForgeByHash(uint256 hash) external view returns (ForgeDefinition memory);

    /// @notice ForgeCreated
    /// @param forge the forge that was created
    event ForgeCreated(ForgeDefinition forge);

    /// @notice ForgeItemAdded
    /// @param item the item that was added
    event ForgeItemAdded(ForgeItemDefinition item);

    /// @notice ItemForged
    /// @param receiver the receiver of the item
    /// @param item the item that was forged
    /// @param quantity the quantity of the item that was forged
    event ItemForged(address receiver, ForgeItemDefinition item, uint256 quantity);

}

contracts/interfaces/IGemPool.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;


import "./IToken.sol";
import "./ITokenPrice.sol";

/// @notice check the balance of earnings and collect earnings
interface IGemPool {

    /// @dev Event generated when a gem is created
    event GemPoolCreated(

        // the gem pool account
        address creator,

        // the gem pool account
        address contractAddress,

        // the gem pool id
        uint256 pool,

        // the gem token definition
        GemPoolSettings gemPoolDefinition

    );

    /// @dev Event generated when a gem is created
    event GemCreated(

        // the gem pool account
        address account,

        // the gem pool id
        uint256 pool,

        // the gem hash
        uint256 gemHash,

        // the gem token definition
        IToken.TokenDefinition tokenDefinition,

        // the gem token quantity
        uint256 quantity

    );

    // mutable pool values
    struct GemPoolData {

        // the gem pool id
        uint256 pool;

        // nnext available gem hash
        uint256 nextGemHash;

        // numbering for the next gem
        uint256 nextGemNumber;

        // total number of gems minted
        uint256 gemsMintedCount;

        // total amount of staked eth
        uint256 totalStakedEth;

    }


    /// @notice staking pool settings - used to confignure a staking pool
    struct GemPoolSettings {

        // the token address we mint on
        address token;

        IToken.TokenSource tokenSource;

        IToken.TokenDefinition tokenDefinition;

        ITokenPrice.TokenPriceData initialPrice;

        // is pool enabled
        bool enabled;

        // is the pool visible
        bool visible;

        // min and max token amounts to stake
        uint256 minTime;

        // max time that the claim can be made
        uint256 maxTime;

        // the difficulty step adjustment (or increase amount if PriceModifier is Static)
        uint256 diffstep;

        // max total number of claims that can be made
        uint256 maxClaims;

        // max quantity per claim
        uint256 maxQuantityPerClaim;

        // max claims per account
        uint256 maxClaimsPerAccount;

        // earn rate for this staking pool
        uint256 earnRatePerPeriod;

        // the number of blocks per earning period
        uint256 earnPeriodBlocks;

        // payout partial blocks
        bool payPartialBlocks;

        // mint the earned token
        bool mintEarnedToken;

        // max total earned amount per stake
        uint256 maxTotalEarnedAmount;

        // allow outright purchases of gems
        bool allowPurchase;

    }

    struct GemPoolStruct {

        GemPoolSettings _gemPoolSettings;
        GemPoolData _gemPoolData;

    }

    /// @notice get the hash of a gem pool
    function gemPoolHash(string memory symbol) external view returns (uint256 _gemPoolHash);
    /// @notice is token a member of the gem pool
    function isMemberOfPool(uint256 gemPoolId, uint256 token) external view returns (bool _isMember);
    /// @notice get the member gems of this pool
    function poolMembers(uint256 gemPoolId) external view returns (uint256[] memory _poolMembers);
    /// get the gem pool symbols in the contract
    function symbols() external view returns (string[] memory _symbols);

}

