//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;


/// @notice interface for a collection of tokens. lists members of collection, allows for querying of collection members, and for minting and burning of tokens.
interface IClaim {


    /// @notice represents a claim on some deposit.
    struct Claim {

        // claim id.
        uint256 id;

        // pool id
        uint256 poolId;

        // the creator of this claim
        address creator;

        // the minter of this claim. This is the contract that minted the item, not the account that created the claim.
        address minter;

        // the amount of eth deposited
        uint256 depositAmount;

        // the type of deposit made. 0 for ETH or an ERC20 token
        address depositToken;

        // the gem quantity to mint to the user upon maturity
        uint256 mintQuantity;

        // the deposit length of time, in seconds
        uint256 depositLength;

        // the block number when this record was created.
        uint256 createdTime;

        // the block number when this record was created.
        uint256 createdBlock;

        // block number when the claim was submitted or 0 if unclaimed
        uint256 claimedBlock;

        // gem hash of minted gem(s) or 0 if no gem minted
        uint256 gemHash;

        // the fee that was paid
        uint256 feePaid;
    }

    /// @notice a set of requirements. used for random access
    struct ClaimSet {

        mapping(uint256 => uint256) keyPointers;
        uint256[] keyList;
        Claim[] valueList;

    }

    struct ClaimSettings {

        ClaimSet claims;

        // the total staked for each token type (0 for ETH)
        mapping(address => uint256) stakedTotal;

    }


    /// @notice emitted when a token is added to the collection
    event ClaimCreated(
        address indexed user,
        address indexed minter,
        Claim claim
    );

    /// @notice emitted when a token is removed from the collection
    event ClaimRedeemed (
        address indexed user,
        address indexed minter,
        Claim claim
    );

    /// @notice create a claim
    /// @param _claim the claim to create
    /// @return _claimHash the claim hash
    function createClaim(Claim memory _claim) external payable returns (Claim memory _claimHash);

    /// @notice submit claim for collection
    /// @param claimHash the id of the claim
    function collectClaim(uint256 claimHash, bool requireMature) external;

    /// @notice return the next claim hash
    /// @return _nextHash the next claim hash
    function nextClaimHash() external view returns (uint256 _nextHash);

    /// @notice get all the claims
    /// @return _claims all the claims
    function claims() external view returns (Claim[] memory _claims);

}

contracts/interfaces/IRequirement.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import "./IToken.sol";
import "../utils/UInt256Set.sol";

/// @notice describes a list of requirements which will be checked against when someone performs an action
interface IRequirement is IToken {

    /// @notice an input requirement
    struct Requirement {

        // the minter that this requirement aplpies to.
        address minter;
        // the token source to match for this requirement. Other a collection or a single token id
        TokenSource source;
        // the quantity of tokens to match for this requirement. must have at least this many tokens
        uint256 quantity;
        // whether or not to take custody of the tokens if the requirement is met
        bool takeCustody;
        // whether or not to burn the tokens if the requirement is met
        bool burn;
        // whether or not to require the exact amount of tokens to match for this requirement
        bool requireExactAmount;

    }

    // data for requirements
    struct RequirementData {

        address token;
        address manager;

        mapping(uint256 => Requirement[]) requirements;
        mapping(uint256 => TokenSet) tokens;

        UInt256Set.Set burnedTokenIds;
        mapping(uint256 => uint256) burnedTokenQuantities;

    }

    struct RequirementSettings {
        RequirementData data;
    }

    /// @notice get all requirements
    /// @return _reqs a set of requirements
    function requirements(uint256 collectionId) external view returns (Requirement[] memory _reqs);

    /// @notice returns whether the specified account meets the requirement at the specified quantity factor
    /// @param account the minter to check
    /// @param req the requirement list to check
    /// @param quantity the quantity factor to check
    /// @return _tokens whether the account meets the requirements
    function fulfillingTokens(address account, Requirement memory req, uint256 quantity) external view returns (Token[] memory _tokens);

    /// @notice returns whether the specified account meets the requirements at the specified quantity factor
    /// @param account the minter to check
    /// @param collectionId the requirement list to check
    /// @param quantity the quantity factor to check
    /// @return _meetsRequirements whether the account meets the requirements
    function meetsRequirements(address account, uint256 collectionId, uint256 quantity) external view returns (bool _meetsRequirements);

    function custodyRequirements(
        uint256 transferId,
        address from,
        address token,
        Requirement[] memory reqs,
        uint256 quantity) external returns(Token[] memory _transferredTokens);

    function releaseRequirements(
        uint256 transferId,
        address token,
        address to) external;
}

contracts/interfaces/IAirdropTokenSale.s.content:
//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.0;

import "./ITokenPrice.sol";
import "./IAirdrop.sol";

/// @notice A token seller is a contract that can sell tokens to a token buyer.
/// The token buyer can buy tokens from the seller by paying a certain amount
/// of base currency to receive a certain amount of erc1155 tokens. the number
/// of tokens that can be bought is limited by the seller - the seller can
/// specify the maximum number of tokens that can be bought per transaction
/// and the maximum number of tokens that can be bought in total for a given
/// address. The seller can also specify the price of erc1155 tokens and how
/// that price increases per successful transaction.
interface IAirdropTokenSale {


    enum PaymentType {
        ETH,
        TOKEN
    }

    /// @notice the settings for the token sale,
    struct TokenSaleSettings {

        // addresses
        address contractAddress; // the contract doing the selling
        address token; // the token being sold
        uint256 tokenHash; // the token hash being sold. set to 0 to autocreate hash
        uint256 collectionHash; // the collection hash being sold. set to 0 to autocreate hash
        // owner and payee
        address owner; // the owner of the contract
        address payee; // the payee of the contract

        string symbol; // the symbol of the token
        string name; // the name of the token
        string description; // the description of the token

        // open state
        bool openState; // open or closed
        uint256 startTime; // block number when the sale starts
        uint256 endTime; // block number when the sale ends

        // quantities
        uint256 maxQuantity; // max number of tokens that can be sold
        uint256 maxQuantityPerSale; // max number of tokens that can be sold per sale
        uint256 minQuantityPerSale; // min number of tokens that can be sold per sale
        uint256 maxQuantityPerAccount; // max number of tokens that can be sold per account

        // inital price of the token sale
        ITokenPrice.TokenPriceData initialPrice;

        PaymentType paymentType; // the type of payment that is being used
        address tokenAddress; // the address of the payment token, if payment type is TOKEN

    }

    /// @notice emitted when a token is opened
    event TokenSaleOpen (uint256 tokenSaleId, TokenSaleSettings tokenSale );

    /// @notice emitted when a token is opened
    event TokenSaleClosed (uint256 tokenSaleId, TokenSaleSettings tokenSale );

    /// @notice emitted when a token is opened
    event TokenPurchased (uint256 tokenSaleId, address indexed purchaser, uint256 tokenId, uint256 quantity );

    // token settings were updated
    event TokenSaleSettingsUpdated (uint256 tokenSaleId, TokenSaleSettings tokenSale );

    /// @notice Get the token sale settings
    /// @return settings the token sale settings
    function getTokenSaleSettings(uint256 tokenSaleId) external view returns (TokenSaleSettings memory settings);

    /// @notice Updates the token sale settings
    /// @param settings - the token sake settings
    function updateTokenSaleSettings(uint256 iTokenSaleId, TokenSaleSettings memory settings) external;

    function initTokenSale(
        TokenSaleSettings memory tokenSaleInit,
        IAirdrop.AirdropSettings[] calldata settingsList
    ) external;
}

